nonIsoTauAnalyzerOutputFiles=( "${tauAnalyzerOutputFilePrefix}muHadNonIsoAnalysis_W1JetsToLNu_${version}.root" "${tauAnalyzerOutputFilePrefix}muHadNonIsoAnalysis_W2JetsToLNu_${version}.root" "${tauAnalyzerOutputFilePrefix}muHadNonIsoAnalysis_W3JetsToLNu_${version}.root" "${tauAnalyzerOutputFilePrefix}muHadNonIsoAnalysis_W4JetsToLNu_${version}.root" )
allTauAnalyzerOutputFiles=( "${tauAnalyzerOutputFilePrefix}muHadAnalysis_W1JetsToLNu_${version}.root" "${tauAnalyzerOutputFilePrefix}muHadAnalysis_W2JetsToLNu_${version}.root" "${tauAnalyzerOutputFilePrefix}muHadAnalysis_W3JetsToLNu_${version}.root" "${tauAnalyzerOutputFilePrefix}muHadAnalysis_W4JetsToLNu_${version}.root" )

#EDM output files
EDMOutputFiles=( "${EDMOutputFilePrefix}W1JetsToLNu${infoTag}_${version}.root" "${EDMOutputFilePrefix}W2JetsToLNu${infoTag}_${version}.root" "${EDMOutputFilePrefix}W3JetsToLNu${infoTag}_${version}.root" "${EDMOutputFilePrefix}W4JetsToLNu${infoTag}_${version}.root" )

#samples
samples=( "W1JetsToLNu" "W2JetsToLNu" "W3JetsToLNu" "W4JetsToLNu" )

####GENERATION LOOP####

#change to working directory
mkdir -p $dir
cd $dir

#loop over number of samples
for i in `seq $iBeg $iEnd`
  do

  #generate cfg file for the isolated sample
  sed -e "s%FILES%${inputFileBlocks[${i}]}%" -e "s%CLEANJETSOUTFILE%${cleanJetsOutFiles[${i}]}%" -e "s%NONISOTAUANALYZEROUTFILE%${nonIsoTauAnalyzerOutputFiles[${i}]}%" -e "s%ALLTAUANALYZEROUTFILE%${allTauAnalyzerOutputFiles[${i}]}%" -e "s%ISOTAUANALYZEROUTFILE%${isoTauAnalyzerOutputFiles[${i}]}%" -e "s%EDMOUTFILE%${EDMOutputFiles[${i}]}%" -e "s%SEQUENCE%process.isoTauAnalysisSequence%" -e "s%PUSCENARIO%S10%" ../tauanalyzer_WNJetsToLNu_Wh1_template_cfg.py > tauanalyzer_${samples[${i}]}_iso_cfg.py

  #generate cfg file for the non-isolated sample
  sed -e "s%FILES%${inputFileBlocks[${i}]}%" -e "s%CLEANJETSOUTFILE%${cleanJetsOutFiles[${i}]}%" -e "s%NONISOTAUANALYZEROUTFILE%${nonIsoTauAnalyzerOutputFiles[${i}]}%" -e "s%ALLTAUANALYZEROUTFILE%${allTauAnalyzerOutputFiles[${i}]}%" -e "s%ISOTAUANALYZEROUTFILE%${isoTauAnalyzerOutputFiles[${i}]}%" -e "s%EDMOUTFILE%${EDMOutputFiles[${i}]}%" -e "s%SEQUENCE%process.nonIsoTauAnalysisSequence%" -e "s%PUSCENARIO%S10%" ../tauanalyzer_WNJetsToLNu_Wh1_template_cfg.py > tauanalyzer_${samples[${i}]}_nonIso_cfg.py

  #generate job submission script for LSF
  cat <<EOF > tauanalyzer_${samples[${i}]}_cfg.sh
#!/bin/bash

jobDir="/afs/cern.ch/user/y/yohay/CMSSW_5_3_3/src/BoostedTauAnalysis/TauAnalyzer/test/$dir"
fileNamePrefix="tauanalyzer_${samples[${i}]}"

cd \$jobDir
eval \`scramv1 runtime -sh\`
cd -
cp \$jobDir/\${fileNamePrefix}_iso_cfg.py \$jobDir/\${fileNamePrefix}_nonIso_cfg.py .
cmsRun \${fileNamePrefix}_iso_cfg.py
cmsRun \${fileNamePrefix}_nonIso_cfg.py
cmsStage -f ${isoTauAnalyzerOutputFiles[${i}]} /store/user/yohay/
cmsStage -f ${nonIsoTauAnalyzerOutputFiles[${i}]} /store/user/yohay/
rm ${isoTauAnalyzerOutputFiles[${i}]} ${nonIsoTauAnalyzerOutputFiles[${i}]} 

exit 0
EOF
  chmod a+x tauanalyzer_${samples[${i}]}_cfg.sh
done

#generate run cfg that runs all files in the directory
cat <<EOF > runWNJetsToLNuTauAnalyzerCfgs.sh
#!/bin/bash

for file in \`ls -alh *W*JetsToLNu*.py | awk '{ print \$9 }'\`
  do
  outFile=\`echo \$file | sed -e "s%\.py%.txt%"\`
  cmsRun \$file > \$outFile
done

exit 0
EOF
chmod a+x runWNJetsToLNuTauAnalyzerCfgs.sh

#generate script that submits all jobs to LSF
cat <<EOF > submitWNJetsToLNuTauAnalyzerJobs.sh
#!/bin/bash

for file in \`ls -alh tauanalyzer*W*JetsToLNu*.sh | awk '{ print \$9 }'\`
  do
  jobName=\`echo \$file | sed -e "s%\.sh%%"\`
  bsub -q 8nh -J \$jobName < \$file
done

exit 0
EOF
chmod a+x submitWNJetsToLNuTauAnalyzerJobs.sh

#generate script that copies all files locally from EOS
cat <<EOF > copyWNJetsToLNuFromEOS.sh
#!/bin/bash

eval \`scramv1 runtime -sh\`
for sample in \`seq 1 4\`
  do
  for cut in Iso NonIso
    do
    cmsStage -f /store/user/yohay/muHad\${cut}Analysis_W\${sample}JetsToLNu_${version}.root /data1/yohay/WNJetsToLNu/analysis/
  done
done

exit 0
EOF
chmod a+x copyWNJetsToLNuFromEOS.sh

exit 0
